mapper.select:
select

from tab_permission
where 1=1

  AND #{id,jdbcType=BIGINT},


  AND #{name,jdbcType=VARCHAR}


  AND type = #{type,jdbcType=VARCHAR}


  AND url = #{url,jdbcType=VARCHAR}


  AND app_name = #{appName,jdbcType=VARCHAR}


  AND available = #{available,jdbcType=VARCHAR}

ORDER BY id ASC
